import {
    BrowserRouter as Router, 
    Link,
  } from "react-router-dom";

  import {GetRecipesList} from "../../../hooks/api/index"
  import {GoBackButton} from "../../../components/Buttons/Goback"


const Recipeslist = (props) => {
return(
    <div  className="xs30 flex backgroundred  just_AR">
        <Link to= {'/recipes/'+ props.id}>
          <div className="xs100 mb20 p20">
            <h1 className="size30">{props.title}</h1>
        </div>
        </Link>
        

    </div>
)}


const Recipes = () => {

/* array de recetas de prueba antes de traerla de la Api 

const recipes = [
    {title:"Jamon con Jamon"},
    {title:"Pimientos del pinchillo"},
    {title:"Rabanos con Rapantes"}];

*/

const recipes = GetRecipesList()

if(!recipes){
    return <div>Loading...</div>;
}

return(
    <div className="xs100 just_AR flex_frw ">
    
         {/* 1ยบ uso {{recipes.map(r =><div>{r}</div> )}*/}
        {/* 2ยบ uso recipes.map(r => <Recipeslist {...r}/> )*/}
    
        {recipes.map(r => <Recipeslist {...r}/>  )}
        <GoBackButton/>
    </div>
    )
};

export default Recipes
