import { useEffect, useState } from "react";

const hostname = 'http://localhost:8000';
/*El options nos permite aplicar los headers a las llamadas asegurandonos de que nuestra comunicacion
es en JSON */
const options = {
    headers:{
        'Content-Type':'application/json',
        'Accepts':'application/json',
    }
}
/*como se repite la misma estructura practicamente en todos los procesos hemos 
creado un UseGET con ella */

export const UseGet = (url) =>{
    const [response,setResponse]=useState();
useEffect ( () => {
        fetch(hostname + url,
        {
            method:'GET',
            ...options,
        }).then(response => response.json())
        .then(response => {
            setResponse(response);
            
        })
    },[url]);
    
    return response;
}


/* 1* uso del get sin el useget para tener como referencia */
/*
export const UseGetRecipesList = () => {
La forma mas basica de traernos el contenido de la api es utilizando un usestate 
para recoger valores y  un useeffect para ejecutar el fetch y dando el valor de 
la response al set 

const [response,setResponse]=useState();
useEffect ( () => {
        fetch('http://localhost:8000/recipes',
        {
            method:'GET',
            ...options,
        }).then(response => response.json())
        .then(response => {
            setResponse(response);
            
        })
    },[]);
    return response;
}

*/

//GET usando useget y implementando el buscador de keywords
/*2 uso del useget en get recipes export const UseGetRecipesList = () => UseGet('/recipes');
}
export const UseGetRecipesList = (keywords) =>{
    let url = '/recipes';
    if(keywords){
     url =  url + '?keywords=' + keywords;
    }
    return UseGet(url);
}
*/
//3* uso del usegetrecipes para un correcto uso 

export const UseGetRecipesList = (listparams) =>{
    let url = '/recipes';
    Object.keys(listparams).map(key => key +'='+ listparams[key]).redude((a,b) => a +'&'+ b)
    if(listparams.keywords){
     url =  url + '?keywords=' + keywords;
    }
    return UseGet(url);
}


export const UseRecipe = (id) => {
    /*La forma mas basica de traernos el contenido de la api es utilizando un usestate para recoger valores y 
 y un useeffect para ejecutar el fetch y dando el valor de la response al set 
 En este segundo caso usamos la id para traernos una receta en concreto por su identificador */
 
 const [response,setResponse]=useState({});
 useEffect ( () => {
         fetch('http://localhost:8000/recipes/'+ id,
         {
             method:'GET',
             ...options,
         }).then(response => response.json())
         .then(response => {
             setResponse(response);  
         })
     },[id]);
     
     return response;
}
//Como el body lo recibimos desde el formulario el retorno del valor no vendra dado en la funcion sino como parametro de retorno
export const UseCreateRecipe = () => {
    const [loading,setloading]= useState(false);
    return {
        createRecipes: (body) => {
            setloading(true);
            return fetch('http://localhost:8000/recipes/',{
                method:'POST',
                body:JSON.stringify(body),
                ...options,
                }).then(response => response.json())
                .then(response =>{
                    setloading(false);
                    return response;
                })
        },
        loading,
    }   
}

 
//HOOK EDIT 

export const UseEditRecipe = (id) => {
    const [loading,setloading]= useState(false);
    return {
        editRecipes: (body) => {
            setloading(true);
            return fetch('http://localhost:8000/recipes/'+ id,{
                method:'PUT',
                body:JSON.stringify(body),
                ...options,
                }).then(response => response.json())
                .then(response =>{
                    setloading(false);
                    return response;
                })
        },
        loading,
    }   
}




export const UseDeleteRecipe = (id) => {
 return () => 
  fetch('http://localhost:8000/recipes/'+ id,
    {
     method:'DELETE',
    })
}
