import {
    BrowserRouter as Router, 
    Link,
    useLocation,
  } from "react-router-dom";

  import {GetRecipesList} from "../../../hooks/api/index"
  import {GoBackButton} from "../../../components/Buttons/Goback"


const Recipeslist = (props) => {

    return(
    <div  className="xs30 mb20 mt20 flex backgroundred just_AR borderadius27">
        <Link to= {'/recipes/'+ props.id}>
          <div className="xs100 mb20 p20 textbox">
            <h1 className="size30">{props.title}</h1>
        </div>
        </Link>
        

    </div>
)}

const parseQueryString = (queryString) => {
    var search = queryString.substring(1);
    return JSON.parse('{"' + decodeURI(search).replace(/"/g, '\\"').replace(/&/g, '","').replace(/=/g,'":"') + '"}')
}

const Recipes = () => {

/* array de recetas de prueba antes de traerla de la Api 

const recipes = [
    {title:"Jamon con Jamon"},
    {title:"Pimientos del pinchillo"},
    {title:"Rabanos con Rapantes"}];

*/

//implementacion del buscador a la lista de recetas
const location= useLocation();
const queryString = parseQueryString(location.search);
const recipes = GetRecipesList()

if(!recipes){
    return <div>Loading...</div>;
}

return(
    <div className="xs100 just_AR flex_frw ">
        {queryString.search || ''}
         {/* 1ยบ uso {{recipes.map(r =><div>{r}</div> )}*/}
        {/* 2ยบ uso recipes.map(r => <Recipeslist {...r}/> )*/}
    
        {recipes.map(r => <Recipeslist {...r}/>  )}
        <GoBackButton/>
    </div>
    )
};

export default Recipes
