import { useEffect, useState } from "react";
import {
    BrowserRouter as Router, 
    Link,
  } from "react-router-dom";


const Recipeslist = (props) => {
return(
    <div>
        <Link to= {'/recipes/'+ props.title}>
          <span>
            {props.title}
        </span>
        </Link>
        

    </div>
)}





const Recipes = () => {

/* array de recetas de prueba antes de traerla de la Api 

const recipes = [
    {title:"Jamon con Jamon"},
    {title:"Pimientos del pinchillo"},
    {title:"Rabanos con Rapantes"}];

*/

const [response,setResponse]=useState();
useEffect ( () => {
    fetch('http://localhost:8000/recipes',
    {
        method:'GET',
    }).then(response => {
        setResponse(response);
    })
})

return(
    <div>
        Recetas:
        {/* 1ยบ uso {{recipes.map(r =><div>{r}</div> )}*/}
        {/* 2ยบ uso recipes.map(r => <Recipeslist {...r}/> )*/}
        {response.map(r => <Recipeslist {...r}/>  )}
    </div>
    )
}



export default Recipes
