import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    useHistory
} from "react-router-dom";


/*En este componente vamos a crear un boton de retorno a la pagina anterior usando el hook useHistory 
que trabaja con el historial de navegacion para poder manipularlo , con las funciones de router podremos 
darle el uso mas adecudo a nuestro caso , actualmente utilzamos con el useHistory la funcion goback
que recogera los datos de la anterior pagina para que podamos retornar a ella.
*/
const GoBackButton = () => {
 let historty = useHistory();
 return (
    <button onClick={historty.goBack}>Atras</button>
 )
}




/*Para ver una receta nuestro componente contiene la const que recogera la id y el Hook useParams para identificar el parametro que llega
lo que nos permitira                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           0sabe cual es la receta y enrutarla gracias al uso de Router */



const OneRecipe = () => {
    const {id} = useParams();
    const recipe =  getRecipe(id);
    return(
        <div>
            Receta:
            {recipe.title}
            
            <GoBackButton/>
        </div>
        )
}
    
    
    
    export default OneRecipe ;
