import './css/App.css';
import './css/estructura.css';
import Myhome from './pages/Home/index';
import Recipes from './pages/Recipes/list/index';
import {OneRecipe} from './pages/Recipes/one_recipe/index';
import HeaderMain  from './components/header/header';
import {Createrecipe} from "./pages/Recipes/new_recipe/newrecipe";
/*Para el uso de router como es logico es necesario importar la libreria , pero en esta 
vemos que ademas de Router y sus componentes tambien estaria BrowserRouter que nos permite
hacerla compatible a otras aplicaciones nativas */
import {
  BrowserRouter as Router,
  Switch,
  Route
} from "react-router-dom";
import { recipeList } from './constants/urls';

/*El comoponente Switch tiene como objetivo matchear las rutas que se encuentren dentro de el.
El Router lleva consigo un path que sera la ruta que se debe cumplir , si la ruta fuera
/pepito si no determinamos el exact se acogera a la ruta / que es la que reconoce como buena 
al poner exact se debe cumplir tal cual y no se acogeria a ninguna ruta.
Cuando tenemos listas y sublista queremos que los matcheos mas concretos estan los primeros dentro del Switch 
por el ello el orden ha de ser desde la ruta mas especifica a la ruta menos especifica.
*/


function App() {
  return (
    <div>
      {/*Nuestro header ha de estar fuera del Router para estar presente en todas las paginas a
      no ser que lo integremos en el router y el header sepa en que ruta esta */}

        <Router>
              <HeaderMain/>
              <Switch>
                <Route path="/recipes/new">
                  <Createrecipe/>
                </Route>
                <Route path={recipebyid}>

                  <OneRecipe/>

                </Route>

                <Route path={recipeList}>

                        <Recipes/>

                </Route>

                <Route exact path="/">

                        <Myhome/>

                  </Route>

              </Switch>

        </Router>


    </div>

  );
}

export default App;
