import { useHistory, useLocation } from "react-router-dom";
import { organizationList ,chargetpointList ,home, organizationof, chargetpointof } from '../../constants/urls';


export const usePages = () => {
    const history = useHistory();
    let location = useLocation();
    const isOrganizationPage = location.pathname.startsWith('/organization/');
    const isOrganizationList = location.pathname === organizationList;
    const isHome = location.pathname === home;
    
    return{
        isOrganizationPage,
        isOrganizationList,
        isHome,
    
        gotolist: search => history.push(organizationList + (search ?('?search='+ search) :'')),
        gotolistcharget: search => history.push(chargetpointList + (search ?('?search='+ search) :'')),
        gotorecipespage: (id) => history.push(organizationof (id)),
        gotochargetpointpage: (id) => history.push(chargetpointof (id)),
        gohome: () => history.push(home)
    }
}
