import { useHistory, useLocation } from "react-router-dom";
import { recipeList ,home, recipeof} from '../../constants/urls';

/*Este Custom Hook nos permite saber en que pagina nos encontramos segun la ruta
para utilizarlo segun nos sea necesario manipularlo
por ejemplo en el header.js lo utilizamos para plasmar o no el boton de atras segun sea 
una pagina u otra*/




export const usePages = () => {
    const history = useHistory();
    let location = useLocation();
    const isRecipesPage = location.pathname.startsWith('/recipes/');
    const isRecipesList = location.pathname ===recipeList;
    const isHome = location.pathname === home;
    
    return{
        isRecipesPage,
        isRecipesList,
        isHome,
        //gotolist: () => history.push(recipeList), PRIMER USO ANTES DE IMPLEMENTAR BUSCADOR 
        gotolist: () => history.push(recipeList + (search ?('&search='+ search) :'')),
        gotorecipespage: (id) => history.push(recipeof(id))
    }
}

//Porque definimos de este modo el gotolist ?¿
