import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCoffee } from '@fortawesome/free-solid-svg-icons';
import {
    BrowserRouter as Router, 
    Link,
    useLocation,
} from "react-router-dom";
import {UseGet, UseGetRecipesList} from "../../../hooks/api/index"
import {GoHomeButton} from "../../../components/Buttons/GoHome"
import { useState } from "react";


const Recipeslist = (props) => {

    return(
    <div  className="xs30 mb20 mt20 flex backgroundred just_AR borderadius27">
        <Link to= {'/recipes/'+ props.id}>
          <div className="xs100 mb20 p20 textbox">
            <h1 className="size30">{props.title}</h1>
        </div>
        </Link>
        

    </div>
)}

const parseQueryString = (queryString) => {
 
    if(!queryString){
        return {};
    }
    
    var search = queryString.substring(1);
    return JSON.parse('{"' + decodeURI(search).replace(/"/g, '\\"').replace(/&/g, '","').replace(/=/g,'":"') + '"}');
} 

const Recipes = () => {

/* array de recetas de prueba antes de traerla de la Api 

const recipes = [
    {title:"Jamon con Jamon"},
    {title:"Pimientos del pinchillo"},
    {title:"Rabanos con Rapantes"}];

*/

//IMPLEMENTACION DE PAGINACION EN EL FRONT 

const[page,setpage]=useState(0);
const[pagesize,setpagesize]=useState(9);

//implementacion del buscador a la lista de recetas
const location= useLocation();
//EL queryString seria la respuesta obtenida del input search tras pulsar el enter y lo necesario para implementar la busqueda
const queryString = parseQueryString(location.search);
/*este querystring se los pasamos al useget incialmente , en segundo lugar le pasamos el queryString y en ultimo paso al actualizar el back 
ahora el contenido viene dentro de un contents por lo que hemos de cambiar lo le pasamos a usegetrecipeslist*/
//const recipes = UseGetRecipesList(queryString.search);
const recipes = UseGetRecipesList({
    keywords:queryString.search,
    page,
    pagesize,
 });



if(!recipes){
    return <div>Loading...</div>;
}

return(
    <div >
       
         {/* 1ยบ uso {{recipes.map(r =><div>{r}</div> )}*/}
        {/* 2ยบ uso recipes.map(r => <Recipeslist {...r}/> )*/}
    <div className="xs100 just_AR flex_frw ">
        {recipes.contents.map(r => <Recipeslist {...r}/>  )}
  
    </div>

     <div>
     Elementos totales :{recipes.totalElements}
        <button>Anterior</button>
        <button>Siguiente</button>
        <FontAwesomeIcon icon={faCoffee} />
        <GoHomeButton/>
     </div>

    </div>
    )
};

export default Recipes
