import { useLocation } from "react-router-dom";

/*Este Custom Hook nos permite saber en que pagina nos encontramos segun la ruta
para utilizarlo segun nos sea necesario manipularlo
por ejemplo en el header.js lo utilizamos para plasmar o no el boton de atras segun sea 
una pagina u otra*/




export const usePages = () => {
    let location = useLocation();
    const isRecipesPage = location.pathname.startsWith('/recipes/');
    const isRecipesList = location.pathname ==='/recipes';
    const isHome = location.pathname === '/';
    return{
        isRecipesPage,
        isRecipesList,
        isHome,
    }
}
