import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    useHistory
} from "react-router-dom";
import {useRecipe} from "../../../api/index";

/*En este componente vamos a crear un boton de retorno a la pagina anterior usando el hook useHistory 
que trabaja con el historial de navegacion para poder manipularlo , con las funciones de router podremos 
darle el uso mas adecudo a nuestro caso , actualmente utilzamos con el useHistory la funcion goback
que recogera los datos de la anterior pagina para que podamos retornar a ella.
*/
const GoBackButton = () => {
 let historty = useHistory();
 return (
    <button onClick={historty.goBack}>Atras</button>
 )
}




/*Para ver una receta nuestro componente contiene la const que recogera la id y el Hook useParams para identificar el parametro que llega
lo que nos permitira recogerlo y utilizarlo                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         0sabe cual es la receta y enrutarla gracias al uso de Router */



export const OneRecipe = () => {
    const {id}= useParams();
    const recipes = useRecipe(id);
 
    return(
        <div>
            Receta:
            {/*recipes && <h1>{recipes.title}</h1>*/}
            <h1>{recipes.title}</h1>
            {/*keywords.map(keywords=><span>{keywords}</span>)NO LO HACE */}
            {recipes.keywords}
            <img style="width:40px" src={recipes.photo}></img>
            <p></p>
            <GoBackButton/>
        </div>
        )
}

/*Al no tener un valor incial recipes es undefine y al determinar un valor en useeffect no renderizaba , 
  de mantener la api sin cambios podriamos hacer la ternaria que esta comentada para renderizarlo cuando tenga valor
  sino lo apropiado es darle un objeto como primer valor al usestate y no tendremos mayor problema , tambien podemos
  utilizar un if condicional para cuando el valor entre como vacio */
