import {
    useParams,
    useHistory,
    Link
} from "react-router-dom";
import {UseRecipe,UseDeleteRecipe} from "../../../hooks/api/index";
import { usePages } from "../../../hooks/usePages/Usepages";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       

export const OneRecipe = () => {
    /*Para ver una receta nuestro componente contiene la const que recogera la id de los parametros de la ruta
     el Hook useParams identifica el parametro lo que nos permitira recogerlo y utilizarlo  */
    const {id}= useParams();
    /*En la variable recipes recogemos esa id y la pasamos por la funcion userecipes que ejecuta el fetch desde la api para traer
    la receta correspondiente*/ 
    const recipes = UseRecipe(id);
    /*Desde el usePages nos traemos la funcion gotolist que nos devolvera a la lista de recetas*/ 
    const {gotolist}=usePages();
    //Al delete podemos pasarle la id que traemos por la ruta facilitando el proceso
    const Deleterecipe = UseDeleteRecipe(id);

    const handledelete = (id) =>{
        Deleterecipe(id).then(response => gotolist())
    }
    return(
        <div className="xs100 just_center">
            <div className="flex_dir_col centrado">
            
            {/*recipes && <h1>{recipes.title}</h1>*/}
            <h1>{recipes.title}</h1>
            {recipes.keywords?.map(keywords=><span>{keywords}</span>)}
            <img alt="" className="xs50" src={recipes.photo}></img>
            <button onClick={handledelete}>Borrar</button>
            <Link to={editrecipeof(":id")}><p>Editar</p></Link>
           
            </div>
        </div>
        )
}

/*Al no tener un valor incial recipes es undefine y al determinar un valor en useeffect no renderizaba , 
  de mantener la api sin cambios podriamos hacer la ternaria que esta comentada para renderizarlo cuando tenga valor
  sino lo apropiado es darle un objeto como primer valor al usestate y no tendremos mayor problema , tambien podemos
  utilizar un if condicional para cuando el valor entre como vacio */
