import { useHistory, useLocation } from "react-router-dom";
import { organizationList ,home, organizationof } from '../../constants/urls';


export const usePages = () => {
    const history = useHistory();
    let location = useLocation();
    const isRecipesPage = location.pathname.startsWith('/recipes/');
    const isRecipesList = location.pathname === organizationList;
    const isHome = location.pathname === home;
    
    return{
        isRecipesPage,
        isRecipesList,
        isHome,
    
        gotolist: search => history.push(organizationList + (search ?('?search='+ search) :'')),
        gotorecipespage: (id) => history.push(organizationof (id)),
        gohome: () => history.push(home)
    }
}
