import { useRef } from "react";
import { UseCreateRecipe } from "../../../hooks/api/index";                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   

export const Createrecipe = () =>{
    /*El uso del useRef por ejemplo con inputs no permite vincular el estado interno del componente
    a una variable de referencia como hemos hecho con title.
    Esto permite que los inputs por ejmeplo , gestionen sus estado interno y preguntarle cuando nos sea necesario
    El contra de todo esto es que a diferencia del onchange no permite controlar los cambios del input */
    const titleref = useRef();
    const keywordsref = useRef();
    /*A diferencia del delete en el que teniamos la id por los parametros 
    ahora los datos de body nos vienen dados desde el formulario por lo que el valor obtenido 
    lo hemos de pasar desde el handle y no previamente como antes*/
    const createRecipes = UseCreateRecipe();
    const handlecreate = () =>{
        const body={
            title:titleref.current.value,
            keywords:keywordsref.current.value.split(','),
        }
        createRecipes(body);
    }
    return(
    <div className="xs100 just_center">
        <div className="flex_dir_col centrado">
    
       
        <h1>Nueva Receta</h1>
        <div className="xs50 flex_dir_col">
            <input type="text" placeholder="Nombre receta" ref={titleref}></input>
            <input type="text" placeholder="Keywords" ref={keywordsref}></input>
            <button className="xs50 ali_center" onClick={handlecreate}>Crear</button>
        </div>
        
            
        </div>
    </div>
    )
}
