import './header.css';
//import {useHistory} from "react-router-dom";
import {usePages} from "../../hooks/usePages/Usepages";

import {
    BrowserRouter as Router, 
    Link,
  } from "react-router-dom";
//cabecera unica para todas las paginas 

function HeaderMain() {
    //let history = useHistory();
    const {isRecipesPage,isRecipesList,isHome,gotolist}= usePages();
    const searchHandler = (e) =>{
        if(e.key === 'Enter'){
            const search = e.target.value;
            gotolist(search);
        }
        console.log(e);
    }


    return (
        <div className="header just_AR">
            
            {isHome && (<h1>HOME</h1>)}
            {isRecipesPage && (<h1>RECETA</h1>)} 
            {isRecipesList && (<h1>LISTA DE RECETAS</h1>)} 
            {<input type="text" onKeyPress={searchHandler}></input>}
            {isRecipesPage && (<button onClick={e=>gotolist()}>Atras</button>)} 
            {isRecipesList && (<Link to="/recipes/new"><p>Crear Receta</p></Link>)} 
             
        </div>
    );
}

/*Para evitar que cuando volvamos atras desde el header la url venga con un object vacio ,
debido a que nosotros para la busqueda lo implementamos en la url de gotolist un search 
que tiene como fin el filtrado o busqueda , pues debemos hacer que sea undefine si no
se esta introduciendo valores de busqueda , por ello la expresion e => gotolist ()
si no le pasamos el evento sera undefined y si entran valores por gotolist de search
ya el propio gotolist nos dara la url */

export default HeaderMain;
