import { useEffect, useState } from "react";

const hostname = 'http://localhost:8000';
/*El options nos permite aplicar los headers a las llamadas asegurandonos de que nuestra comunicacion
es en JSON */
const options = {
    headers:{
        'Content-Type':'application/json',
        'Accepts':'application/json',
    }
}
/*como se repite la misma estructura practicamente en todos los procesos hemos 
creado un UseGET con ella*/
