//mejora del useget

export const UseGet = (url) =>{
    const [response,setResponse]=useState();
    const [loading,setloading]=useState(true);
    const [error,seterror]=useState();
    //estado refresh que nos permitira forzar el estado 
    const[forcererender,setforcererender]=useState(0);
useEffect ( () => {
        setloading(true);
        fetch(hostname + url,
        {
            method:'GET',
            ...options,
        }).then(response => response.json())
        .then(response => {
            setResponse(response);
            setloading(false);
        }).catch(error=>{
            setloading(false);
            seterror(error);
        })
    },[url , forcererender]);
    const refresh = setforcererender(forcererender+1);
    
    return {
        response,
        loading,
        error,
        refresh,
    }
}

/* 1* uso del get sin el useget para tener como referencia */
