//3* uso del usegetrecipes para trabajar correctamente con las urls y gestion de errores 

export const UseGetRecipesList = (keywords) =>{
    let url = '/recipes';
  
    //metodo elaborado usando Object ,encode y reduce
    //const querystring = Object.keys(listparams).map(key => key +'='+ encodeURIComponent(listparams[key])).redude((a,b) => a +'&'+ b)
    
    /*el const params recomponemos el objeto , primero sacamos las claves , segundo en un filter excluimos las undefined
     tercero tenemos las claves que no son undefined , necesitamos el objeto entero denuevo por lo que usamos el reduce 
     a la derecha de todo tenemos el objeto vacio ,y por cada clave que seria prev ponme la clave y dame el valor  */
     const params = Object.keys(keywords)
     .filter(key=>keywords[key] !==undefined)
     .reduce((prev ,next) => ({...prev,[next]:keywords[next]}),{});
     
     //Metodo estandarizado usando urlsearchparams
     const querystring = new URLSearchParams(params).toString();
    
    if(querystring){
     url =  url + '?' + querystring;
    
    }
    return UseGet(url);
}


export const UseRecipe = (id) => {
    /*La forma mas basica de traernos el contenido de la api es utilizando un usestate para recoger valores y 
 y un useeffect para ejecutar el fetch y dando el valor de la response al set 
 En este segundo caso usamos la id para traernos una receta en concreto por su identificador */
 
 const [response,setResponse]=useState({});
 useEffect ( () => {
         fetch('http://localhost:8000/recipes/'+ id,
         {
             method:'GET',
             ...options,
         }).then(response => response.json())
         .then(response => {
             setResponse(response);  
         })
     },[id]);
     
     return response;
}
//Como el body lo recibimos desde el formulario el retorno del valor no vendra dado en la funcion sino como parametro de retorno
export const UseCreateRecipe = () => {
    const [loading,setloading]= useState(false);
    return {
        createRecipes: (body) => {
            setloading(true);
            return fetch('http://localhost:8000/recipes/',{
                method:'POST',
                body:JSON.stringify(body),
                ...options,
                }).then(response => response.json())
                .then(response =>{
                    setloading(false);
                    return response;
                })
        },
        loading,
    }   
}

 
//HOOK EDIT 

export const UseEditRecipe = (id) => {
    const [loading,setloading]= useState(false);
    return {
        editRecipes: (body) => {
            setloading(true);
            return fetch('http://localhost:8000/recipes/'+ id,{
                method:'PUT',
                body:JSON.stringify(body),
                ...options,
                }).then(response => response.json())
                .then(response =>{
                    setloading(false);
                    return response;
                })
        },
        loading,
    }   
}




export const UseDeleteRecipe = (id) => {
 return () => 
  fetch('http://localhost:8000/recipes/'+ id,
    {
     method:'DELETE',
    })
}
