import { useEffect, useState } from "react";

//componente que se comunica con el backend de express
export const getRecipesList = () => {
   /*La forma mas basica de traernos el contenido de la api es utilizando un usestate para recoger valores y 
y un useeffect para ejecutar el fetch y dando el valor de la response al set */
const [response,setResponse]=useState();
useEffect ( () => {
        fetch('http://localhost:8000/recipes',
        {
            method:'GET',
        }).then(response => response.json())
        .then(response => {
            setResponse(response);
            
        })
    },[]);
    return response;
}


export const getRecipe = (id) => {
    /*La forma mas basica de traernos el contenido de la api es utilizando un usestate para recoger valores y 
 y un useeffect para ejecutar el fetch y dando el valor de la response al set */
 
 const [response,setResponse]=useState();
 useEffect ( () => {
         fetch('http://localhost:8000/recipes/'+ id,
         {
             method:'GET',
         }).then(response => response.json())
         .then(response => {
             setResponse(response);
            
           
         })
     },[]);
     return response;
}
